import React, { Component } from 'react'
import './index.css'

export default class Demo extends Component {
    render() {
        return (
            <div className="a">
                <h1>我是主组件</h1>
                <B>
                    <C />
                </B>
            </div>
        )
    }
}

class B extends Component {
    
    state = {name: 'tom'}

    render() {
        console.log(this.props);
        return (
            <div className="b">
                <h1>我是B组件</h1>
                {/*  获取标签体中的内容 */}
                {/* {(children) => children} */}
                {this.props.children}
            </div>
        )
    }
}

class C extends Component {
    render() {
        return (
            <div className="c">
                <h1>我是C组件</h1>
            </div>
        )
    }
}
