import React, { Component } from 'react'
import './index.css'

export default class index extends Component {
    state = {count: 0}

    handleClick = () => {
        console.log(111);
        //获取原来的count 值
        const {count} = this.state;
        // 更新状态
        this.setState({count: count+1})
    }

    render() {
        return (
            <div>
                <h1>当前求和为 {this.state.count}</h1>
                <button onClick={this.handleClick}>+</button>
            </div>
        )
    }
}
