import React, { Component } from 'react'
import './index.css'

// 定义全局变量
const MyCotent = React.createContext();
const { Provider } = MyCotent;
class A extends Component {
    state = { username: 'tom' }

    render() {
        // UserNameContext = this.state.username;
        return (
            <div className="parent">
                <h1>A组件</h1>
                <h4>我的用户名{this.state.username}</h4>
                <Provider value={this.state.username}>
                    <B />
                </Provider>
                
            </div>
        )
    }
}

class B extends Component {
    render() {
        return (
            <div className="child">
                <h1>B组件</h1>
                <h4>我的用户名</h4>
                <C />
            </div>
        )
    }
}


class C extends Component {
    render() {
        //获取组件传递的内容对象
        console.log(this.context);
        return (
            <div className="grand">
                <h1>C组件</h1>
                <h4>我的用户名</h4>
                <D />
            </div>
        )
    }
}

class D extends Component {
    render() {
        return (
            <div className="five">
                <h1>D组件</h1>
                <h4>我的用户名???</h4>
            </div>
        )
    }
}


export default A
