import React, { Component } from 'react'
import './index.css'

class A extends Component {
    state = { username: 'tom' }

    render() {
        return (
            <div >
                <h1>A组件</h1>
                <h4>我的用户名{this.state.username}</h4>
                <B/>
            </div>
        )
    }
}

class B extends Component {
    render() {
        return (
            <div >
                <h1>B组件</h1>
                <h4>我的用户名???</h4>
                <C/>
            </div>
        )
    }
}


class C extends Component {
    render() {
        return (
            <div >
                <h1>C组件</h1>
                <h4>我的用户名???</h4>
                <D/>
            </div>
        )
    }
}

class D extends Component {
    render() {
        return (
            <div >
                <h1>D组件</h1>
                <h4>我的用户名???</h4>
            </div>
        )
    }
}


export default A
