import React, { Component } from 'react'
import './index.css'


//优化代码
export default class Demo extends Component {
    state = { carName: "奔驰" }

    HandleCar = () => {
        // console.log("dsad");
        this.setState({ carName: '宝马' })
    }
    
    shouldComponentUpdate(nextProps,nextState) {
        // nextProps 表示下一个要更新的props  
        // nextState 表示下一个要更新的state 
        console.log(nextProps,nextState);
        console.log(this.props,this.state);   
        
        if(this.props.carName === nextProps) 
            return false | true;
    }
    
    render() {
        const { carName } = this.state;
        return (
            <div>
                <h3 className="parament">{this.state.carName}</h3>
                <button onClick={this.HandleCar} style={{ cursor: 'pointer' }}>点击换车</button>
                <Child carName={carName} />
            </div>
        )
    }
}


class Child extends Component {
    render() {
        const { carName } = this.props;
        return (
            <div>
                <h3 className="child">子模块</h3>
                <h6>我接到的车是:{carName}</h6>
            </div>
        )
    }
}
