import React, { Component } from 'react'

export default class Hooks extends Component {

    state = {count: 0}

    add = () => {
        this.setState((state) =>{
           return  {count: state.count + 1}
        })
    }

    render() {
        return (
            <div>
                <h2>当前求和为: {this.state.count}</h2>
                <button onClick={this.add}>+</button>
            </div>
        )
    }
}
